import React, { useState } from 'react';
import './chat.css';  // Import styling for chat page

const Chat = () => {
  const [messages, setMessages] = useState([
    { text: "Welcome! I'm here to assist you with any medical questions you might have.", sender: "bot" },
  ]);
  const [userInput, setUserInput] = useState("");

  const handleSendMessage = () => {
    if (userInput.trim()) {
      // Add user message to chat
      setMessages([...messages, { text: userInput, sender: "user" }]);
      setUserInput("");

      // Bot's response based on keywords
      let botResponse = "I'm here to assist you. Please share your symptoms or medical concerns.";

      if (userInput.toLowerCase().includes("fever")) {
        botResponse = "It seems like you may have a fever. You should monitor your temperature and consult a healthcare professional.";
      } else if (userInput.toLowerCase().includes("headache")) {
        botResponse = "A headache can have many causes. Stay hydrated and rest. If it persists, consider seeing a doctor.";
      } else if (userInput.toLowerCase().includes("fatigue")) {
        botResponse = "Fatigue can be caused by various factors. Make sure you are getting enough sleep and staying hydrated.";
      }

      // Add bot response to chat
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: botResponse, sender: "bot" },
      ]);
    }
  };

  // Handle the Enter key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  // Reset chat
  const handleResetChat = () => {
    setMessages([{ text: "Welcome! I'm here to assist you with any medical questions you might have.", sender: "bot" }]); // Reset to initial state
    setUserInput("");
  };

  return (
    <div className="chat">
      {/* Displaying the welcome message with a larger font */}
      <div className="welcome-message">
        Welcome! I'm here to assist you with any medical questions you might have.
      </div>

      <div className="chat-box">
        {messages.map((message, index) => (
          <div key={index} className={message.sender}>
            <strong>{message.sender === "user" ? "Patient:" : "Chatbot:"}</strong>
            <p>{message.text}</p>
          </div>
        ))}
      </div>

      <div className="input-container">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyDown={handleKeyPress}  // Add the key press event listener
          placeholder="Type your message..."
        />
        <button className="send-chat-button" onClick={handleSendMessage}>Send</button>
      </div>

      {/* Reset Button outside the input container */}
      <div className="reset-container">
        <button className="reset-chat-button" onClick={handleResetChat}>Reset Chat</button>
      </div>
    </div>
  );
};

export default Chat;
