import React, { useState } from 'react';
import './chat.css';  // Import styling for chat page
import axios from "axios";

const Chat = () => {
  const [messages, setMessages] = useState([
    { text: "Welcome! I'm here to assist you with any medical questions you might have.", sender: "bot" },
  ]);
  const [userInput, setUserInput] = useState("");

  const handleSendMessage = async () => {
    if (userInput.trim()) {
      // 构造新的消息数组，先添加用户消息
      const newMessages = [...messages, { text: userInput, sender: "user" }];
      // 更新前端显示
      setMessages(newMessages);
      setUserInput("");

      try {
        // 发送 POST 请求到 FastAPI /chat 接口
        // 请求体需要包含 messages 字段，后端根据这个数组构建对话上下文
        const response = await axios.post("http://localhost:8000/chat", { messages: newMessages });
        // 后端返回的数据格式例如：{ botResponse: { content: "机器人回复内容", role: "assistant" } }
        const botResponse = response.data.botResponse;
        // 将后端返回的回答添加到消息列表中
        setMessages((prevMessages) => [...prevMessages, { text: botResponse.content, sender: "bot" }]);
      } catch (error) {
        console.error("Error calling backend API:", error);
        setMessages((prevMessages) => [
          ...prevMessages,
          { text: "Sorry, an error occurred while processing your message.", sender: "bot" },
        ]);
      }
    }
  };
  // Handle the Enter key press
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  // Reset chat
  const handleResetChat = () => {
    setMessages([{ text: "Welcome! I'm here to assist you with any medical questions you might have.", sender: "bot" }]); // Reset to initial state
    setUserInput("");
  };

  return (
    <div className="chat">
      {/* Displaying the welcome message with a larger font */}
      <div className="welcome-message">
        Welcome! I'm here to assist you with any medical questions you might have.
      </div>

      <div className="chat-box">
        {messages.map((message, index) => (
          <div key={index} className={message.sender}>
            <strong>{message.sender === "user" ? "Patient:" : "Chatbot:"}</strong>
            <p>{message.text}</p>
          </div>
        ))}
      </div>

      <div className="input-container">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyDown={handleKeyPress}  // Add the key press event listener
          placeholder="Type your message..."
        />
        <button className="send-chat-button" onClick={handleSendMessage}>Send</button>
      </div>

      {/* Reset Button outside the input container */}
      <div className="reset-container">
        <button className="reset-chat-button" onClick={handleResetChat}>Reset Chat</button>
      </div>
    </div>
  );
};

export default Chat;
